def printStar(n):

    if n == 3:
        array = [[]for i in range(3)]
        for i in range(9):
            if i != 4:
                array[i//3].append('*')
            else:
                array[i//3].append(' ')

        return array

    array = [[]for i in range(3)]
    for i in range(9):
        if i != 4:
            array[i//3].append(printStar(n//3))
        else:
            array[i//3].append(blank(n))

    return array


def blank(n):
    if n == 3:
        return [' ', ' ', ' ']
    return [[blank(n//3)]for i in range(3)]


n = int(input())
star = [[]for _ in range(n)]
